mapper.insert:
insert into article
	set regDate = now()
		, updateDate = now()
		, memberId = #{loginedMemberId}
		, boardId = #{boardId}
		, title = #{title} 
		, `body` = #{body}

mapper.select:
SELECT a.* , m.nickname AS `writer` FROM article AS a INNER JOIN `member` AS m ON a.memberId = m.id where a.boardId = #{boardId }
AND a.title like CONCAT('%', #{searchKeyword }, '%')
AND a.body like CONCAT('%', #{searchKeyword }, '%')
AND ( a.title like CONCAT('%', #{searchKeyword }, '%') OR a.body like CONCAT('%', #{searchKeyword }, '%') )
ORDER BY id DESC LIMIT #{limitStart}, ${itemsInAPage}

mapper.update:
update article

		updateDate = now()

			, title = #{title}


			, `body` = #{body}


	where id = #{id}
